    "Current Stock": currentStock,
    "Is Enough": isEnough,
    "Material Name":$input.first().json['Material Name'],
"Unit":$input.first().json['Measurement Unit'],
"Minimum Stock Level": $input.first().json['Minimum Stock Level']
  }
};

const currentStock = parseFloat($input.item.json["Current Stock"]);
const minStock = parseFloat($input.item.json["Minimum Stock Level"]);

// Check if stock is below minimum
const isLow = currentStock < minStock;

return {
  json: {
    ...$input.item.json,
    "Is Low": isLow,
    "Alert Message": isLow ? 
      `Low stock alert: ${$input.item.json["Material Name"]} (ID: ${$input.item.json["Product ID"]}) - Current Stock: ${currentStock} ${$input.item.json["Measurement Unit"]}, Minimum: ${minStock}` 
      : null
  }
};

